import org.openqa.selenium.WebDriver;
import org.openqa.selenium.chrome.ChromeDriver;
import org.testng.annotations.AfterClass;
import org.testng.annotations.BeforeClass;
import org.testng.annotations.Test;
import org.openqa.selenium.chrome.ChromeOptions;
import java.time.Duration;
import io.github.bonigarcia.wdm.WebDriverManager;
import org.openqa.selenium.support.ui.WebDriverWait;
import org.openqa.selenium.support.ui.ExpectedConditions;
import com.aventstack.extentreports.ExtentReports;
import com.aventstack.extentreports.ExtentTest;
import com.aventstack.extentreports.Status;
import com.charitableimpact.config.ExtentReportManager;

public class GroupEditTest {
    WebDriver driver;
    WebDriverWait wait;
    ExtentReports extent;
    ExtentTest test;

    @BeforeClass
    public void setup() {
        WebDriverManager.chromedriver().setup();
        ChromeOptions options = new ChromeOptions();
        options.addArguments("--start-maximized");
        driver = new ChromeDriver(options);
        wait = new WebDriverWait(driver, Duration.ofSeconds(10));
        extent = ExtentReportManager.getExtent();
    }

    @Test
    public void editGroupNameAndValidate() {
        test = ExtentReportManager.createTest("Edit Group Name and Validate");

        driver.get("https://my.charitableimpact.com/users/login");

        LoginPage loginPage = new LoginPage(driver);
        loginPage.enterEmail("menon.sanu@gmail.com");
        loginPage.enterPassword("Test123#");
        loginPage.clickLoginButton();
        test.log(Status.INFO, "Logged in to Charitable Impact");

        driver.get("https://my.charitableimpact.com/groups/test-group-by-sanu-updated-by-nandu/edit");

        GroupEditPage groupEditPage = new GroupEditPage(driver);
        groupEditPage.editGroupName("Cheehoo");
        groupEditPage.clickSaveButton();
        test.log(Status.INFO, "Edited group name to 'Cheehoo' and saved");

        // Validating the group name edited
        wait.until(ExpectedConditions.textToBePresentInElementValue(groupEditPage.groupNameInput, "Cheehoo"));
        test.log(Status.PASS, "Group name successfully edited to 'Cheehoo'");

    }

    @AfterClass
    public void teardown() {
        driver.quit();
        ExtentReportManager.flush();
    }
}
